import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import Pagination from '@material-ui/lab/Pagination';


const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      marginTop: theme.spacing(2),
    },
  },
}));

export const PageNavigation = ({ history, searchText, setStart, start, numberResultsFound }) => {

  // const handleChange = event => {
  //   setStart(event.target.value);
  // };
  const numberPages = Math.floor(numberResultsFound / 20)
  const classes = useStyles();

  const calculateCurrentPage = (currentNumber) => {
    return Math.floor(currentNumber / 20)
  }

  const onNextPage = () => {
    // getData();
    setStart(start + 20)
    history.push(`/search?q=${searchText}&start=${start + 20}`);
    window.scrollTo(0, 0)
  }

  const onPreviousPage = () => {
    // getData();
    setStart(start - 20)
    history.push(`/search?q=${searchText}&start=${start - 20}`);
    window.scrollTo(0, 0)
  }

  const handleChange = (event, value) => {
    console.log(value)
    setStart((value-1) * 20);
    history.push(`/search?q=${searchText}&start=${(value-1) * 20}`);
    // getData();
    window.scrollTo(0, 0)
    console.log(start)
  };

  
  // if (start === 0) {
  //   return (
  //     <div>
  //       <button onClick={onNextPage}>Next</button>
  //     </div>
  //   );
  // }
  // else if ((start + 20) > numberResultsFound) {
  //   return (
  //     <div>
  //       <button onClick={onPreviousPage}>Previous</button>
  //     </div>
  //   )
  // }
  // else {
  //   return (
  //     <div>
  //       <button onClick={onPreviousPage}>Previous</button> <button onClick={onNextPage}>Next</button>
  //     </div>
  //   );
  // }

  return (
    <div className={classes.root}>
      <Pagination
        size="large"
        count={numberPages}
        boundaryCount={1}
        siblingCount={3}
        page={calculateCurrentPage(start+20)}
        onChange={handleChange}
      />
    </div>
  );


}
