import React, { useState, useEffect } from 'react';
import { Switch, Route, useHistory } from "react-router-dom"
import axios from "axios";

import { LandingPage } from "./Containers/LandingPage"
import { SearchingPage } from "./Containers/SearchingPage"

export const App = () => {
  const [results, setResults] = useState([]);
  const [page, setPage] = useState(0);
  const [word, setWord] = useState("");
  const history = useHistory();

  const getData = async () => {
    console.log({ page })
    const reply = await axios({
      method: 'get',
      url: 'http://127.0.0.1:5000/api',
      params: {
        query: 'binary search trees',
        start: page
      }
    });
    console.log(reply)
    setResults(reply.data.results)
    setPage(page + 20);
  }

  useEffect(() => {
    getData();
  }, [])

  const onNextPage = () => {
    console.log({ page })
    getData();
    history.push(`/search?page=${page}`);
    window.scrollTo(0, 0)

  }

  return (
    <div>
      <Switch>
        <Route exact path="/" render={() => <LandingPage word={word} setWord={setWord} />} />
        <Route path="/search" render={(props) => <SearchingPage results={results} {...props} />} />
      </Switch>
      <button onClick={onNextPage}>next</button>
    </div>
  );
}
