import React, { useState } from 'react';
import { useHistory } from "react-router-dom"
import logo from '../assets/gugol-300.png';
import SearchBar from '../Components/SearchBar'
import { makeStyles } from '@material-ui/core/styles';
import { Box, Button } from '@material-ui/core/';


const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      margin: theme.spacing(2),
      display: 'flex',
    },
  },
  search: {
    margin: theme.spacing(3),
  },
  button: {
    textTransform: 'none'
  },
}));

export const LandingPage = () => {

  const classes = useStyles();
  const [searchText, setSearchText] = useState("");
  const history = useHistory()

  const onSearch = () => {
    history.push(`/search?q=${searchText}&start=0`);
    window.scrollTo(0, 0)
  }

  return (
    <div className={classes.root}>
      <Box marginTop="500" marginX="auto" alignItems="center" justifyContent="center" width="100%" className={classes.search}>
        <img src={logo} alt="Gugol" />
      </Box>
      <Box m="auto" alignItems="center" justifyContent="center" width="100%" className={classes.search}>
        <SearchBar searchText={searchText} setSearchText={setSearchText} onSearch={onSearch} />
      </Box>
      <Box m="auto" alignItems="center" justifyContent="center" width="100%" className={classes.search}>
        <Button className={classes.button} variant="contained" onClick={onSearch}>Gugol Search</Button>
      </Box>
    </div>
  );
}
