import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import Pagination from '@material-ui/lab/Pagination';
import { Box } from '@material-ui/core/';


const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      marginTop: theme.spacing(2),
      marginBottom: theme.spacing(5),
      marginLeft: theme.spacing(15),
      textAlign: 'center',
    },
  },
}));

export const PageNavigation = ({ history, searchText, setStart, start, numberResultsFound }) => {

  const classes = useStyles();
  const numberPages = Math.floor(numberResultsFound / 20)
  const calculateCurrentPage = (currentNumber) => {
    return Math.floor(currentNumber / 20)
  }

  const onNextPage = () => {
    // getData();
    setStart(start + 20)
    history.push(`/search?q=${searchText}&start=${start + 20}`);
    window.scrollTo(0, 0)
  }

  const onPreviousPage = () => {
    // getData();
    setStart(start - 20)
    history.push(`/search?q=${searchText}&start=${start - 20}`);
    window.scrollTo(0, 0)
  }

  const handleChange = (event, value) => {
    console.log(value)
    setStart((value - 1) * 20);
    history.push(`/search?q=${searchText}&start=${(value - 1) * 20}`);
    window.scrollTo(0, 0)
  };


  return (
    <Box mr="auto" width="100%" maxWidth={740}>
      <div className={classes.root}>
        <Pagination
          size="large"
          count={numberPages}
          boundaryCount={1}
          siblingCount={3}
          page={calculateCurrentPage(start + 20)}
          onChange={handleChange}
        />
      </div>
    </Box>

  );


}
