import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router-dom"
import { ListResult } from "../Components/ListResult"
import axios from "axios";


export const SearchingPage = ({ searchText }) => {

	const [results, setResults] = useState([]);
	const [start, setStart] = useState(0);
	const history = useHistory();

	const getParams = () => {
		console.log(history.location.search.split("?q="))
		const firstSplit = history.location.search.split("?q=");
		const params = {
			query: firstSplit[1].split("&")[0].replace("%20", " "),
			start: firstSplit[1].split("&")[1].split("=")[1]
		}
		console.log({ params })

		// {
		// 	q:{},
		// 	start: 
		// }
		// const params = {
		// 	q: history.location.search.split("&")
		// }
	}

	const getData = async () => {
		console.log({ start })
		const reply = await axios({
			method: 'get',
			url: 'http://127.0.0.1:5000/api',
			params: {
				query: searchText,
				start: start
			}
		});
		console.log(reply)
		setResults(reply.data.results)
		setStart(start + 20);
	}
	useEffect(() => {
		getParams();
		getData();
		// eslint-disable-next-line
	}, [])

	const onNextPage = ({ searchText, }) => {
		console.log({ start })
		getData();
		history.push(`/search?q=${searchText}&start=${start}`);
		window.scrollTo(0, 0)

	}

	return <>{results.map(result =>
		<ListResult
			key={result.url}
			url={result.url}
			title={result.title}
			snippet={result.snippet}
		/>
	)}
		<button onClick={onNextPage}>Next</button>
	</>
}
