import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';



const useStyles = makeStyles(theme => ({
	root: {
		flexGrow: 1,
	},
	paper: {
		padding: theme.spacing(0.4),
		marginBottom: theme.spacing(2),
		marginLeft: theme.spacing(16),
		textAlign: 'left',
		fontFamily: 'Arial',
		color: theme.palette.text.secondary,
	},
	url: {
		color: '#3c4043',
		fontSize: 12
	},
	title: {
		color: '#1a0db2',
		fontSize: 18
	},
	snippet: {
		color: '#3c4043',
		fontSize: 14
	},
}));

export const ListResult = ({ url, title, snippet }) => {
	const classes = useStyles();

	if (title.length > 59) {
		title += "...";
	}

	if (snippet.length > 349) {
		snippet = snippet.slice(0, 200) + "...";
	}

	const formattedUrl = url.replace(/\//g, " › ");

	return (
		<div className={classes.root}>
			<Box line-height={5} mr="auto" width="100%" maxWidth={700}>
				<Paper elevation={1} className={classes.paper}>
					<span className={classes.url}>{formattedUrl}<br /></span>
					<a className={classes.title} href={`//${url}`}>{title}</a>
					<p className={classes.snippet}>{snippet}</p>
				</Paper>
			</Box>
		</div>
	)
}
