import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router-dom"
import { ListResult } from "../Components/ListResult"
import axios from "axios";


export const SearchingPage = () => {

	const [results, setResults] = useState([]);
	const [querySpeed, setQuerySpeed] = useState(null)
	const [numberResultsFound, setNumberResultsFound] = useState(null)
	// const [start, setStart] = useState(0);
	const history = useHistory();

	const getParams = () => {
		const decoded = decodeURI(history.location.search).split(/^(\?q=)?/)[2].split("&");
		console.log(decoded)

		const firstSplit = history.location.search.split(/^(\?q=)?/);
		const params = {
			query: decoded[0],
			start: decoded[1].replace("start=", ""),
			// query: firstSplit[1].split("&")[0].replace("%20", " "),
			// start: firstSplit[1].split("&")[1].split("=")[1]
		}
		console.log(params.query)
		console.log(params.start)
		return params;
	}

	const getData = async () => {
		const { query, start } = getParams();
		const reply = await axios({
			method: 'get',
			url: 'http://127.0.0.1:5000/api',
			params: {
				query: query,
				start: start
			}
		});
		console.log(reply)
		setResults(reply.data.results)
		setQuerySpeed(reply.data.query_speed)
		setNumberResultsFound(reply.data.number_results_found)
		// setStart(start + 20);
	}

	useEffect(() => {
		getData();
		// eslint-disable-next-line
	}, [])

	// const onNextPage = ({ searchText, }) => {
	// 	console.log({ start })
	// 	getData();
	// 	history.push(`/search?q=${searchText}&start=${start}`);
	// 	window.scrollTo(0, 0)

	// }

	return <>{results.map(result =>
		<ListResult
			key={result.url}
			url={result.url}
			title={result.title}
			snippet={result.snippet}
		/>
	)}
		{/* <button onClick={onNextPage}>Next</button> */}
	</>
}
