import React from "react";
import logo from "../assets/gugol-90.png"
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputAdornment from "@material-ui/core/InputAdornment";
import SearchIcon from "@material-ui/icons/Search";
import Grid from '@material-ui/core/Grid';
import { Box, FormControl } from '@material-ui/core/';


const useStyles = makeStyles(theme => ({
  root: {
    border: "1px solid red"
  },
  margin: {
    margin: theme.spacing(1)
  },
  withoutLabel: {
    marginTop: theme.spacing(3)
  },
  textField: {
    width: "80%",
    marginTop: theme.spacing(1.5)
  },
  height: {
    height: 45
  },
  img: {
    padding: theme.spacing(2)
  },
  input: {
    cursor: "pointer"
  }
}));

export default function TopBar({ searchText, setSearchText, onSearch }) {
  const classes = useStyles();

  const handleChange = event => {
    setSearchText(event.target.value);
  };

  return (
    <Grid container className={classes.root}>
      <Grid item>
        <a href="./">
          <img className={classes.img} src={logo} alt="Homepage Gugol" />
        </a>
      </Grid>
      <Grid item style={{ border: "1px solid green" }}>
        <FormControl
          className={clsx(classes.margin, classes.textField)}
          variant="outlined"
        >
          <form
            onSubmit={(e) => {
              e.preventDefault();
              onSearch();
            }}
          >
            <OutlinedInput className={classes.height}
              type="search"
              value={searchText}
              onChange={handleChange}
              fullWidth
              endAdornment={
                <InputAdornment position="end" onClick={onSearch} className={classes.input}>
                  <SearchIcon />
                </InputAdornment>
              }
            />
          </form>
        </FormControl>
      </Grid>
    </Grid>
  );
}
