import React, { useState } from 'react';
import { useHistory } from "react-router-dom"
import logo from '../assets/gugol-300.png';
import SearchBar from '../Components/SearchBar'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import { Box, Button } from '@material-ui/core/';


const useStyles = makeStyles(theme => ({
  root: {
    border: "1px solid red",
    // height: "100vh"
  },
  logo: {
    margin: theme.spacing(3),
    marginTop: theme.spacing(15),
    border: "1px solid blue"

  },
  search: {
    margin: theme.spacing(3),
    marginTop: theme.spacing(3),
    border: "1px solid orange"
  },
  button: {
    textTransform: 'none',
    border: "1px solid green"
  },
}));

export const LandingPage = () => {

  const classes = useStyles();
  const [searchText, setSearchText] = useState("");
  const history = useHistory()

  const onSearch = () => {
    history.push(`/search?q=${searchText}&start=0`);
    window.scrollTo(0, 0)
  }

  return (
    <Grid container direction="column" justifyContent="center" alignItems="center" className={classes.root}>
      <Grid item>
        <img src={logo} alt="Gugol" />
      </Grid>
      <Grid item>
        <SearchBar searchText={searchText} setSearchText={setSearchText} onSearch={onSearch} />
      </Grid>
      <Grid item>
        <Button disableElevation={true} className={classes.button} variant="contained" onClick={() => searchText.length > 3 && onSearch()}>Gugol Search</Button>
      </Grid>
    </Grid>
    //   <div className={classes.root}>
    //     <Box m="auto" alignItems="center" justifyContent="center" width="100%" className={classes.logo}>
    //     </Box>
    //     <Box m="auto" alignItems="center" justifyContent="center" width="100%" className={classes.search}>
    //     </Box>
    //     <Box m="auto" alignItems="center" justifyContent="center" width="100%" className={classes.search}>
    //     </Box>
    //   </div>
  );
}
