import React from "react";
import logo from "../assets/gugol-90.png"
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputAdornment from "@material-ui/core/InputAdornment";
import SearchIcon from "@material-ui/icons/Search";
import { Box, Container, FormControl } from '@material-ui/core/';


const useStyles = makeStyles(theme => ({
  root: {
    marginBottom: 10,
  },
  margin: {
    margin: theme.spacing(1)
  },
  withoutLabel: {
    marginTop: theme.spacing(3)
  },
  textField: {
    width: "80%",
    marginTop: theme.spacing(2)
  },
  height: {
    height: 40
  },
  img: {
    padding: theme.spacing(2)
  },
  box: {
    marginTop: theme.spacing(2),
  }
}));

export default function TopBar({ searchText, setSearchText, onSearch }) {
  const classes = useStyles();

  const handleChange = event => {
    setSearchText(event.target.value);
  };

  return (
    <div className={classes.root}>
      <Box mr="auto" width="100%" maxWidth={730} className={classes.box}>
        <a href="./">
          <img className={classes.img} src={logo} alt="Homepage Gugol" />
        </a>
        <FormControl
          className={clsx(classes.margin, classes.textField)}
          variant="outlined"
        >
          <form
            onSubmit={(e) => {
              e.preventDefault();
              onSearch();
            }}
          >
            <OutlinedInput className={classes.height}
              type="search"
              value={searchText}
              onChange={handleChange}
              fullWidth
              endAdornment={
                <InputAdornment position="end">
                  <SearchIcon />
                </InputAdornment>
              }
            />
          </form>
        </FormControl>
      </Box>
    </div>
  );
}
