import React, { useState } from 'react';
import { useHistory } from "react-router-dom"
import logo from '../assets/gugol-300.png';
import SearchBar from '../Components/SearchBar'
import { makeStyles } from '@material-ui/core/styles';
import { Box, Button } from '@material-ui/core/';


const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
  search: {
    margin: theme.spacing(3),

  },
}));

export const LandingPage = () => {

  const classes = useStyles();
  const [searchText, setSearchText] = useState("");
  const history = useHistory()

  const onSearch = () => {
    history.push(`/search?q=${searchText}&start=0`);
    window.scrollTo(0, 0)
  }

  return (
    <div className={classes.root}>
      <img src={logo} alt="Gugol" />
      <Box mr="auto" width="100%" maxWidth={730} className={classes.search}>
      <SearchBar searchText={searchText} setSearchText={setSearchText} onSearch={onSearch} />
      </Box>
      <Button variant="contained" onClick={onSearch}>Search</Button>
    </div>

  );
}
