import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import forEach from "lodash/forEach"

const useStyles = makeStyles(theme => ({
	root: {
		flexGrow: 1,
	},
	paper: {
		padding: theme.spacing(1.5),
		marginBottom: theme.spacing(2),
		marginRight: theme.spacing(1.5),
		textAlign: 'left',
		fontFamily: 'Arial',
		color: theme.palette.text.secondary,
	},
	url: {
		color: '#3c4043',
		fontSize: 13,
	},
	title: {
		color: '#1a0db2',
		fontSize: 19,
		lineHeight: 1.8,
		textDecoration: 'none',
		'&:hover': {
			cursor: "pointer",
			textDecoration: 'underline',
		},
	},
	snippet: {
		color: '#3c4043',
		fontSize: 15,
		lineHeight: 1.5,
		wordBreak: "break-word"
	},
}));

export const ListResult = ({ url, title, snippet, searchLemmatized }) => {

	const classes = useStyles();

	if (title === null) {
		title = url.slice(0, 59) + "..."
	}
	else if (title === "" && snippet !== null && snippet !== "") {
		title = snippet.slice(0, 59) + "..."
	}
	else if (title.length > 59) {
		title += "...";
	}

	if (snippet === null) {
		snippet = url
	}
	else if (snippet === "" && title !== null && title !== "") {
		snippet = title.slice(0, 200) + "..."
	}
	else if (snippet !== null && snippet.length > 349) {
		snippet = snippet.slice(0, 200) + "...";
	}

	let shortUrl = url

	if (url.length > 80) {
		shortUrl = url.slice(0, 75) + "...";
	}

	const formattedUrl = shortUrl.replace(/\//g, " › ");


	forEach(searchLemmatized, (word) => {
		console.log({ word })
		snippet = snippet.replace(RegExp(`\\b${word}\\b`, 'g'), `\u003c\u0062\u003e${word}\u003c\u002f\u0062\u003e`);
	})

	console.log({ snippet })

	const boldWords = (text) => {
		let nodeBold = null;
		text.split(" ").forEach((word, index) => {
			console.log({ word })
			if (index === 3) {
				nodeBold += <b>{word}</b>
			}
			nodeBold += ` ${word}`
		})
		return <div>{nodeBold}</div>
		// return <div>Suck my balls \u003c\u0062\u003eJack\u003c\u002f\u0062\u003e please</div >
	}

	return (
		<div className={classes.root}>
			<Box mr="auto" width="100%" maxWidth={730}>
				<Paper elevation={1} className={classes.paper}>
					<Typography className={classes.url}>
						{formattedUrl}
					</Typography>
					<Typography className={classes.title} onClick={() => window.location.href = `//${url}`}>
						{title}
					</Typography >
					<Typography className={classes.snippet}>
						{boldWords(snippet)}
						{/* <div >
							{snippet}
						</div> */}
					</Typography>
				</Paper >
			</Box >
		</div >
	)
}
