import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router-dom"
import { ListResult } from "../Components/ListResult"
import { PageNavigation } from "../Components/PageNavigation"
import { makeStyles } from '@material-ui/core/styles';
import TopBar from "../Components/TopBar"
import axios from "axios";


export const SearchingPage = () => {

	const [results, setResults] = useState([]);
	const [querySpeed, setQuerySpeed] = useState([])
	const [numberResultsFound, setNumberResultsFound] = useState([])
	const [searchText, setSearchText] = useState([])
	const [start, setStart] = useState(0);
	const history = useHistory();

	const getParams = () => {
		const decoded = decodeURI(history.location.search).split(/^(\?q=)?/)[2].split("&");
		const params = {
			query: decoded[0],
			start: decoded[1].replace("start=", ""),
		}
		return params;
	}

	const getData = async () => {
		const { query, start } = getParams();
		const reply = await axios({
			method: 'get',
			url: 'http://127.0.0.1:5000/api',
			params: {
				query: query,
				start: start
			}
		});
		console.log(reply)
		setResults(reply.data.results)
		setSearchText(query)
		setQuerySpeed(reply.data.query_speed)
		setNumberResultsFound(reply.data.number_results_found)
	}

	const onSearch = () => {
		history.push(`/search?q=${searchText}&start=${start}`);
		window.scrollTo(0, 0)
	}

	useEffect(() => {
		getData();
		// eslint-disable-next-line
	}, [])

	return (
		<>
			<TopBar
				searchText={searchText}
				setSearchText={setSearchText}
				onSearch={onSearch}
			/>
			<div className={useStyles.stats}>About {numberResultsFound.toLocaleString()} results ({querySpeed} seconds)</div>
			{results.map(result =>
				<ListResult
					key={result.url}
					url={result.url}
					title={result.title}
					snippet={result.snippet}
				/>
			)}
			<PageNavigation
				history={history}
				searchText={searchText}
				setStart={setStart}
				start={start}
				numberResultsFound={numberResultsFound}
			/>
		</>
	)
}


const useStyles = makeStyles(theme => ({
	stats: {
		color: '#3c4043',
		fontSize: 15,
		lineHeight: 1.5
	},
}));
