import React from "react";
import { useHistory } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputAdornment from "@material-ui/core/InputAdornment";
import SearchIcon from "@material-ui/icons/Search";
import Grid from '@material-ui/core/Grid';
import { Box, FormControl } from '@material-ui/core/';

import logo from "../assets/gugol-90.png"

const useStyles = makeStyles(theme => ({
  img: {
    padding: theme.spacing(2)
  },
  searchContainer: {
    border: "1px solid green",
    flex: 1
  },
  textField: {
    margin: theme.spacing(1),
    flex: 1,
    marginTop: theme.spacing(1.5)
  },
  withoutLabel: {
    marginTop: theme.spacing(3)
  },
  height: {
    height: 45
  },
  input: {
    cursor: "pointer"
  }
}));

export default function TopBar({ searchText, setSearchText, onSearch }) {
  const classes = useStyles();
  const history = useHistory();

  const handleChange = event => {
    setSearchText(event.target.value);
  };

  return (
    <Grid container>
      <Grid alignItems="center" item onClick={() => history.push("/")} style={{ border: "1px solid yellow", display: "flex" }}>
        <img src={logo} alt="Homepage Gugol" style={{ border: "1px solid black" }} />
      </Grid>
      <Grid item className={classes.searchContainer} >
        <FormControl
          className={classes.textField}
          variant="outlined"
        >
          <form
            onSubmit={(e) => {
              e.preventDefault();
              onSearch();
            }}
          >
            <OutlinedInput className={classes.height}
              type="search"
              value={searchText}
              onChange={handleChange}
              fullWidth
              endAdornment={
                <InputAdornment position="end" onClick={onSearch} className={classes.input}>
                  <SearchIcon />
                </InputAdornment>
              }
            />
          </form>
        </FormControl>
      </Grid>
    </Grid>
  );
}
