import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';



const useStyles = makeStyles(theme => ({
	root: {
		flexGrow: 1,
	},
	paper: {
		padding: theme.spacing(1.5),
		marginBottom: theme.spacing(2),
		marginLeft: theme.spacing(16),
		textAlign: 'left',
		fontFamily: 'Arial',
		color: theme.palette.text.secondary,
	},
	url: {
		color: '#3c4043',
		fontSize: 12,
	},
	title: {
		color: '#1a0db2',
		fontSize: 18,
		lineHeight: 1.8
	},
	snippet: {
		color: '#3c4043',
		fontSize: 14,
		lineHeight: 1
	},
}));

export const ListResult = ({ url, title, snippet }) => {
	const classes = useStyles();

	if (title.length > 59) {
		title += "...";
	}

	if (snippet.length > 349) {
		snippet = snippet.slice(0, 200) + "...";
	}

	let shortUrl = url

	if (url.length > 120) {
		shortUrl = url.slice(0, 120) + "...";
	}

	const formattedUrl = shortUrl.replace(/\//g, " › ");

	return (
		<div className={classes.root}>
			<Box mr="auto" width="100%" maxWidth={720}>
				<Paper elevation={1} className={classes.paper}>
					<span className={classes.url}>{formattedUrl}</span><br />
					<a className={classes.title} href={`//${url}`}>{title}</a><br />
					<span className={classes.snippet}>{snippet}</span>
				</Paper>
			</Box>
		</div>
	)
}
